import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class IconStarCustom extends StatelessWidget {  final String calificacionItem   ;  final int cantidadEstrellas  ;  const IconStarCustom({   this.calificacionItem,   this.cantidadEstrellas});  @override  Widget build(BuildContext context) {    //--- Armamos una lista de acuerdo a la cantidad de estrellas    List<Widget> iconos = new List<Widget>();    for( int i = 0; i < this.cantidadEstrellas; i++ ){      iconos.add( new Icon( Icons.star ) );    }    //---- Si la cantidad de Iconos es menor a 5 completamos los faltantes con iconos vacios    if( iconos.length < 5 ){      int iconosFaltantes = 5 - iconos.length;      for( int i = 0; i < iconosFaltantes; i++ ){        iconos.add( new Icon( Icons.star_border ) );      }    }    return Row(      children:[        Text( this.calificacionItem ),        Row(          children: iconos,        )      ]    );  }}