import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class IconStarCustom extends StatelessWidget {  //---- Constantes para utilizar  final Color colorActivo   = Colors.blue;  final Color colorInactivo = Colors.black12;  final double defaultSize  = 12;  final String calificacionItem   ;  final int cantidadEstrellas  ;  final Alignment alignment;  const IconStarCustom({   this.calificacionItem,   this.cantidadEstrellas,   this.alignment});  @override  Widget build(BuildContext context) {    //--- Armamos una lista de acuerdo a la cantidad de estrellas    List<Widget> iconos = new List<Widget>();    for( int i = 0; i < this.cantidadEstrellas; i++ ){      iconos.add(          new Container(              child: Icon( Icons.star , color: colorActivo, size: defaultSize,)          )      );    }    //---- Si la cantidad de Iconos es menor a 5 completamos los faltantes con iconos vacios    if( iconos.length < 5 ){      int iconosFaltantes = 5 - iconos.length;      for( int i = 0; i < iconosFaltantes; i++ ){        iconos.add( new Icon( Icons.star, color: colorInactivo, size: defaultSize, ) );      }    }    return Container(        alignment: this.alignment,      child: Row(          children:[            Container(                margin: EdgeInsets.fromLTRB(0, 0, 5, 0),                child: Text(                  this.calificacionItem,                  style: TextStyle(                    fontSize: defaultSize,                  ),                )            ),            Row(              children: iconos,            )          ]      ),    );  }}