import 'package:flutter/material.dart';import 'package:practica04/widget/icon-star-custom.dart';class ItemListCustom extends StatelessWidget{   final String descripcionItem        ;  final String calificacionItem       ;  final int    cantidadEstrellasItem  ;  final String imagen;  const ItemListCustom({    this.descripcionItem,    this.calificacionItem,    this.cantidadEstrellasItem,    this.imagen    });    @override  Widget build(BuildContext context) {    return Container(      //--- Customizer Component      //-----------------------------      height: 80,      padding: EdgeInsets.all(10),      margin: EdgeInsets.symmetric(vertical: 5, horizontal: 20),      decoration: BoxDecoration(        borderRadius: BorderRadius.all( Radius.circular( 5 ) ),        color: Colors.white60      ),      //-----------------------------      child: Row(        children: [          Container(            margin: EdgeInsets.symmetric( horizontal: 15 ),            child: Image.asset( imagen ),          ),          Container(            margin: EdgeInsets.symmetric( horizontal: 15 ),            alignment: AlignmentDirectional.topEnd,            child: Column(              children: [                Text( this.descripcionItem ),                IconStarCustom(                  calificacionItem: this.calificacionItem,                  cantidadEstrellas: this.cantidadEstrellasItem,                )              ],            ),          ),          Text( this.descripcionItem ),        ],      ),    );  }}